function speech(textToSpeech) {
    // Texto del elemento con ID = message
    var text = $(textToSpeech).text();
    var msg = new SpeechSynthesisUtterance();

    msg.rate = 0.88;
    msg.pitch = 1;
    msg.text = text;
    msg.lang = 'en-AU';

    msg.onend = function(e) {
        console.log('Finished in ' + event.elapsedTime + ' seconds.');

    };

    speechSynthesis.speak(msg);

};


var array = ['#messageName', '#message1content', '#message2title', '#message2content1', '#message2content2', '#message2content3',
'#message2content4', '#message2content5', '#message2content6', '#message3title', '#message3content1', '#message3content2'
, '#message3content3', '#message3content4'];

array.forEach(function(element) {
    speech(element)
  });

